class Solution:
    def wordBreak(self, s, wordDict):
        dp = {}
        dp_c = {}
        trie = {}
        max_len_word = 0
        for word in wordDict:
            dp[word] = True
            for c in set(list(word)):
                dp_c[c] = True
            max_len_word = max(len(word), max_len_word)
            for i in range(1, len(word) + 1):
                trie[word[:i]] = word[:i + 1]

        # Character check
        for c in set(list(s)):
            if not dp_c.get(c):
                return False

        idx = 0
        idx2 = 0
        last_idx = []
        last_idx2 = []
        last_reset = -1
        reset = False
        while idx < len(s):
            lim = min(max_len_word, len(s) - idx)
            while True:
                if idx + idx2 > len(s)-1:
                    return False
                word = s[idx:idx + idx2 + 1]
                if not trie.get(word):
                    reset = True
                    break
                if dp.get(word):  # if word is found, go on to find next word
                    last_idx.append(idx)
                    last_idx2.append(idx2)
                    # Auto reinitialise
                    idx += idx2 + 1
                    idx2 = 0
                    reset = False
                    break
                elif idx2 == lim - 1:  # pull trigger to backtrack
                    reset = True
                    if last_reset == idx2:
                        return False
                    else:
                        last_reset = idx2
                    break
                idx2 += 1
            if idx + idx2 >= len(s):
                return True
            if reset:
                if not last_idx or not last_idx2:
                    return False
                idx = last_idx.pop()
                idx2 = last_idx2.pop() + 1
        return True


from functools import lru_cache
class Solution:
    def wordBreak(self, s, wordDict):
        @lru_cache()
        def wordFind(start):
            if start == len(s):
                return True
            else:
                for end in range(start+1, len(s)+1):
                    word = s[start:end]
                    if word in set(wordDict) and wordFind(end):
                        return True
        return wordFind(0)

